<template lang="html">    <div class="wrapper" :id="Notes.id" >        <button class="button-delete" @click="deleteNotes">            <div class="button-delete__div button-delete__transform-one"></div>            <div class="button-delete__div button-delete__transform-two"></div>        </button>        <div class="wrapper-click">            <router-link class="all-link" :to="{name:'notes',params:{ id: Notes.id,head:txtHeader,Text:Texts,index: index}}">                <div class="notes" :id="Notes.id">                    <div class="notes-nav">                        <div class="notes-text-div">                            <h3 class="notes-text__header">{{txtHeader}}</h3>                            <ul class="notes-ul">                                <li v-for="(txts,index) in Texts" class="notes-li__txt">                                    <p class="txt-notes" :index="index">{{txts}}</p></li>                            </ul>                        </div>                    </div>                </div>            </router-link>        </div>    </div></template><script>    import redactnotes from "./redactnotes.vue";    export default {        props: ['headerTxt','IDheader','saveTexts','saveIndex',],        data() {            return {                txtHeader:this.headerTxt ,                saveTxt: this.saveTexts,                saveInd: this.saveIndex,                Texts: [],                Notes: {                    id: this.IDheader,                    txt: [],                },                AllNots: [],                index: [],                child: this.IDheader,            }        },        components:{            redactnotes        },        methods: {           async addNotes(){                this.Texts = this.saveTxt                await  this.Texts                this.index = this.saveInd                let search = document.getElementById(this.Notes.id)                let txtNotes = search.querySelectorAll('.txt-notes')                    this.index.forEach(i=>{                        txtNotes[i].classList.add('lineThroug')                    })            },            deleteNotes(){             console.log(this)                this.$emit('deleteNotes',this.Notes.id)            }        },       async mounted(){            debugger            if(this.saveTxt != "" &&  this.saveTxt != undefined && this.saveTxt  != null){             await   this.addNotes()            }            let notes = {                    id: this.Notes.id,                    index: this.index,                    texts: this.Texts,                    header: this.txtHeader                }            if(this.Notes.id === 'fake'){               let not = document.getElementById(this.Notes.id)                not.classList.add('not-visible')            }            this.$emit('saveNot',notes)        },        created() {            debugger        }    }</script><style scoped>    .notes-nav{        display: flex;        flex-direction: column;    }    .notes{        display: flex;        flex-direction: column;        align-items: center;        justify-items: center;        background: cornsilk ;        border-radius: 10%;        padding: 10px;    }    .notes-li__txt:focus{        outline: none !important;    }    .notes-input{        margin: 7% 0;    }    .all-link{        text-decoration: none;    }    .all-link:link{        color: inherit;    }    .all-link:visited{        color: inherit;    }    .not-visible{        visibility: hidden;        position: absolute;    }</style>