<template lang="html" @setHeader="setHeader">    <div class="wrapper" :id="Notes.id">        <button class="button-delete" @click="deleteNotes">            <div class="button-delete__div button-delete__transform-one"></div>            <div class="button-delete__div button-delete__transform-two"></div>        </button>        <div class="wrapper-click">            <router-link class="all-link"                         :to="{name:'notes',params:{ id: Notes.id,head:txtHeader,Text:Texts,ind: index,goLink:goLink }}">                <div class="notes" :id="Notes.id">                    <div class="notes-nav">                        <div class="notes-text-div">                            <h3 class="notes-text__header" v-for="head in txtHeader">{{head}}</h3>                            <ul class="notes-ul">                                <li v-for="(txts,index) in Texts.slice(0,3)" class="notes-li__txt">                                    <p class="txt-notes" :index="index">{{txts}}</p></li>                            </ul>                            <p v-show="showAll" class="show-all">{{numberNots}}</p>                        </div>                    </div>                </div>            </router-link>        </div>    </div></template><script>    import redactnotes from "./redactnotes.vue";    export default {        props: ['headerTxt', 'IDheader', 'saveTexts', 'saveIndex',],        data() {            return {                txtHeader: [],                saveTxt: this.saveTexts,                saveInd: this.saveIndex,                Texts: [],                showAll: false,                Notes: {                    id: this.IDheader,                    txt: [],                },                AllNots: [],                index: [],                child: this.IDheader,                goLink:true,                deleteTempalte:                    `<div id="delete-notes">                        <button class="delete-notes-button" data-delete="1">Yes</button>                       <button class="delete-notes-button" data-delete="0">No</button>                    </div>`            }        },        components: {            redactnotes        },        computed: {          numberNots: function() {              let returnSHow = 'show all'               if (this.Texts.length > 3) {                   this.showAll = true                   return returnSHow               } else {                   this.showAll = false                   return  0               }          }        },        methods: {            async addNotes() {                this.Texts = this.saveTxt                await this.Texts                this.index = this.saveInd                let search = document.getElementById(this.Notes.id)                let txtNotes = search.querySelectorAll('.txt-notes')                this.index.forEach(i => {                    if (i < 3) {                        txtNotes[i].classList.add('lineThroug')                    }                })            },            deleteNotes() {                document.body.insertAdjacentHTML('afterbegin', this.deleteTempalte)                let button = document.querySelectorAll('.delete-notes-button')                let deleteNot = document.getElementById('delete-notes')                let width = deleteNot.offsetWidth                let windowWidth = window.innerWidth                deleteNot.style.left = `${(windowWidth / 2) - width / 2}px`                button.forEach(el => {                    el.addEventListener('click', (event) => {                        if (event.target.dataset.delete === '1') {                            deleteNot.parentNode.removeChild(deleteNot)                            this.$emit('deleteNotes', this.Notes.id)                        } else if (event.target.dataset.delete === '0') {                            deleteNot.parentNode.removeChild(deleteNot)                        }                    })                })            }        },        async mounted() {            this.txtHeader.push(this.headerTxt)            debugger            if (this.saveTxt != "" && this.saveTxt != undefined && this.saveTxt != null) {                await this.addNotes()            }            let notes = {                id: this.Notes.id,                index: this.index,                texts: this.Texts,                header: this.txtHeader[0]            }            if (this.Notes.id === 'fake') {                let not = document.getElementById(this.Notes.id)                not.classList.add('not-visible')            }            this.$emit('saveNot', notes)        },        created() {            debugger        }    }</script><style scoped>    .notes-nav {        display: flex;        flex-direction: column;    }    .notes {        display: flex;        flex-direction: column;        align-items: center;        justify-items: center;        background: cornsilk;        border-radius: 10%;        padding: 10px;    }    .notes-li__txt:focus {        outline: none !important;    }    .notes-input {        margin: 7% 0;    }    .all-link {        text-decoration: none;    }    .all-link:link {        color: inherit;    }    .all-link:visited {        color: inherit;    }    .show-all {      color:  #59A2CC    }    .not-visible {        visibility: hidden;        position: absolute;    }</style>