<template lang="html"><div class="wrapper">    <router-link :to="{name:'notes',params:{ id: Notes.id,head:txtHeader,Text:Texts}}">    <div class="notes" :id="Notes.id" >            <div class="notes-nav">                <div class="notes-text-div">                    <h3 class="notes-text__header">{{txtHeader}}</h3>                    <ul class="notes-ul">                        <li v-for="(txts,index) in Texts" class="notes-li__txt">                            <label class="label" :for="index">{{txts}}</label> <input  class="input-check" type="checkbox" :id="index"></li>                    </ul>                </div>            </div>    </div>        </router-link>        <router-view class="background"></router-view></div></template><script>    export default {        props: ['headerTxt'],        data() {            return {                txtHeader:this.headerTxt ,                Texts: [],                Notes: {                    id: this.headerTxt,                    txt: [],                },                AllNots: [],                index: '',            }        },        methods: {            inputHeader() {                if (event.keyCode === 13) {                    this.$el.blur()                }            },            redactNotes(header, text,index) {                let notesId = document.getElementById(this.Notes.id)                let input = notesId.querySelectorAll('.input-check');                this.txtHeader = header;                this.Texts = [];                this.Texts = text;                this.index = index                input.forEach(el=>{                    el.checked = false                });                index.forEach(i=>{                    input[i].checked = true                })                debugger            },        },        mounted() {        },        created() {            this.txtHeader = this.headerTxt;        }    }</script><style scoped>    .notes-nav{        display: flex;        flex-direction: column;    }    .notes{        display: flex;        flex-direction: column;        align-items: center;        justify-items: center;        background: cornsilk ;        border-radius: 10%;        padding: 10px;    }    .notes-li__txt:focus{        outline: none !important;    }    .notes-input{        margin: 7% 0;    }    .background{        background: cornsilk;    }</style>