<template>    <div class="wrapper-notes">        <div class="notes-text-div" id="redactThisNotes">            <span v-show="statusRedact">Redact</span>            <h3 class="notes-text__header notes__contentEdit"   @blur="endInputHeader($event)">{{txtHeader}}</h3>            <ul class="notes-ul__txt-red">                <li v-for="(txts,index) in Texts" class="notes-li__txt notes-li__txt-red" :index="index"  @blur="endInput($event)" >                    <p class="label notes__contentEdit" :for="index + txtHeader">{{txts}}</p> <input class="input-check-redact" type="checkbox" :id="index + txtHeader">                </li>            </ul>            <input class="notes-input"  v-model="txt" v-show="showInput"                   @blur="end">        </div>        <button class="notes-plus" @click="Create"> Add notes</button>        <button class="notes-plus" @click="Edit"> Edit</button>        <button class="notes-save" @click="save">Save notes</button>    </div>    </template><script>    export default {        data() {            return {                txt: '',                txtHeader: '',                showInput:false,                statusRedact:false,                checkeds: [],                Texts: [],            }        },        methods:{            Create(){                this.showInput = true            },            __GetCheck(){                let search = document.querySelector("#redactThisNotes")                let input   = search.querySelectorAll('.input-check-redact');                let current = 0                input.forEach(el =>{                    if (el.checked){                        this.checkeds.push(current)                        current++                    } else {                        current++                    }                })                return this.checkeds            },            save(){                let input = document.querySelectorAll(".input-check-redact")                this.checkeds = [];                this.$parent.redactNotes(this.txtHeader,this.Texts,this.__GetCheck())                input.forEach(el=>{                    el.remove()                })                history.back()            },            endInputHeader(e){                this.txtHeader = e.currentTarget.textContent;            },            endInput(e){                  let index =+e.currentTarget.attributes.index.value                this.Texts[index] = e.currentTarget.textContent            },            end(){                this.Texts.push(this.txt);                let input = document.querySelectorAll('.input-check-redact');                this.txt = null;                this.showInput = false            },            Edit(){                let notesId = document.getElementById('#redactThisNotes')                let elem = notesId.querySelectorAll('.label')                console.log(elem[0].htmlFor)                this.redact = !this.redact;                if(this.redact === true){                    this.statusRedact = true                    elem.forEach(el =>{                        el.contenteditable = true                    })                } else {                    this.statusRedact = false                    let current = 0                    this.statusRedact = false                    elem.forEach(el =>{                        el.contenteditable = false                    })                }            }        },        mounted() {        },        created() {            this.txtHeader = this.$route.params.id;            this.Texts = this.$route.params.Text        }    }</script><style scoped>    .wrapper-notes{        position: absolute;        width: 50%;        left: 35%;        top:50%;    }</style>